mapper.select:

select rownum, bno, title, id, content, regdate, hit, replycnt
from (
	select @rownum:=@rownum+1 as rownum, b.* 
	from (
		select @rownum:=0
		) as tmp, mb_board as b
	order by bno desc
) as boardlist
where board_name = #{board_name}

	and rownum > (#{pageNum} - 1) * #{amount} and rownum <= #{pageNum} * #{amount}




		and title like concat('%', #{keyword}, '%')  	


		and content like concat('%', #{keyword}, '%')


		and id like concat('%', #{keyword}, '%')

mapper.update:
update mb_board
set title=#{title},
	content=#{content}
where bno=#{bno}

update mb_board
set replycnt = replycnt + #{amount} 
where bno = #{bno}
